-- Tabela para armazenar informações dos alunosCREATE TABLE Aluno (    ID INT PRIMARY KEY,    nome VARCHAR(100),    matricula VARCHAR(100),    turma VARCHAR(100),);-- Tabela para armazenar informações das disciplinasCREATE TABLE Disciplinas (    DisciplinaID INT PRIMARY KEY,    Nome VARCHAR(100));-- Tabela para armazenar as notas dos alunos em cada disciplinaCREATE TABLE Notas (    AlunoID INT,    DisciplinaID INT,    Nota DECIMAL(4, 2),    PRIMARY KEY (AlunoID, DisciplinaID),    FOREIGN KEY (AlunoID) REFERENCES Alunos(AlunoID),    FOREIGN KEY (DisciplinaID) REFERENCES Disciplinas(DisciplinaID));